import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { getAllCountries, getCountriesByName, getCountriesByRegion } from '../services/api';
import { AuthContext } from '../App';

const HomePage = () => {
  const [countries, setCountries] = useState([]);
  const [filteredCountries, setFilteredCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRegion, setSelectedRegion] = useState('');
  const { user, logout } = useContext(AuthContext);

  // Regions for filter dropdown
  const regions = ['Africa', 'Americas', 'Asia', 'Europe', 'Oceania'];

  // Fetch all countries on component mount
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        setLoading(true);
        const data = await getAllCountries();
        setCountries(data);
        setFilteredCountries(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch countries');
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  // Handle search input change
  const handleSearchChange = async (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.trim() === '') {
      // If search is empty, reset to all countries or filtered by region
      if (selectedRegion) {
        const regionFiltered = countries.filter(country => 
          country.region === selectedRegion
        );
        setFilteredCountries(regionFiltered);
      } else {
        setFilteredCountries(countries);
      }
    } else {
      // Search by name
      try {
        let results;
        if (selectedRegion) {
          // If region is selected, filter the search results by region
          results = await getCountriesByName(value);
          results = results.filter(country => country.region === selectedRegion);
        } else {
          results = await getCountriesByName(value);
        }
        setFilteredCountries(results);
      } catch (err) {
        setFilteredCountries([]);
      }
    }
  };

  // Handle region filter change
  const handleRegionChange = async (e) => {
    const region = e.target.value;
    setSelectedRegion(region);

    if (region === '') {
      // If no region selected, show all countries or filtered by search
      if (searchTerm) {
        try {
          const results = await getCountriesByName(searchTerm);
          setFilteredCountries(results);
        } catch (err) {
          setFilteredCountries([]);
        }
      } else {
        setFilteredCountries(countries);
      }
    } else {
      // Filter by region
      try {
        let results = await getCountriesByRegion(region);

        // If there's also a search term, filter by name within the region
        if (searchTerm) {
          results = results.filter(country => 
            country.name.common.toLowerCase().includes(searchTerm.toLowerCase())
          );
        }

        setFilteredCountries(results);
      } catch (err) {
        setFilteredCountries([]);
      }
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navigation */}
      <nav className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-gray-800">Countries Explorer</h1>
            </div>
            <div className="flex items-center">
              {user ? (
                <div className="flex items-center">
                  <span className="mr-4">Welcome, {user.name}</span>
                  <button
                    onClick={logout}
                    className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700 transition duration-300"
                  >
                    Logout
                  </button>
                </div>
              ) : (
                <div className="flex items-center space-x-4">
                  <Link
                    to="/login"
                    className="px-4 py-2 rounded border border-blue-600 text-blue-600 hover:bg-blue-50 transition duration-300"
                  >
                    Sign In
                  </Link>
                  <Link
                    to="/register"
                    className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700 transition duration-300"
                  >
                    Sign Up
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Search and Filter */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="flex flex-col md:flex-row gap-4 mb-8">
          <div className="w-full md:w-2/3">
            <input
              type="text"
              placeholder="Search for a country..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
          <div className="w-full md:w-1/3">
            <select
              value={selectedRegion}
              onChange={handleRegionChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">Filter by Region</option>
              {regions.map(region => (
                <option key={region} value={region}>{region}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Countries Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filteredCountries.length > 0 ? (
            filteredCountries.map(country => (
              <Link 
                to={`/country/${country.cca3}`} 
                key={country.cca3}
                className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition duration-300"
              >
                <div className="h-40 overflow-hidden">
                  <img
                    src={country.flags.png}
                    alt={`Flag of ${country.name.common}`}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="p-4">
                  <h2 className="text-lg font-bold mb-2 text-gray-800">{country.name.common}</h2>
                  <div className="text-sm text-gray-600">
                    <p><span className="font-semibold">Population:</span> {country.population.toLocaleString()}</p>
                    <p><span className="font-semibold">Region:</span> {country.region}</p>
                    <p><span className="font-semibold">Capital:</span> {country.capital?.[0] || 'N/A'}</p>
                  </div>
                </div>
              </Link>
            ))
          ) : (
            <div className="col-span-full text-center py-10">
              <p className="text-gray-500 text-lg">No countries found matching your criteria.</p>
            </div>
          )}
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-white shadow-inner py-6 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <p className="text-center text-gray-500">
            &copy; {new Date().getFullYear()} Countries Explorer. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
