import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { getCountryByCode } from '../services/api';
import { AuthContext } from '../App';

const CountryDetailPage = () => {
  const { code } = useParams();
  const [country, setCountry] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCountry = async () => {
      try {
        setLoading(true);
        const data = await getCountryByCode(code);
        setCountry(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch country details');
        setLoading(false);
      }
    };

    fetchCountry();
  }, [code]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error || !country) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <div className="text-red-500 text-xl mb-4">{error || 'Country not found'}</div>
        <button 
          onClick={() => navigate('/')}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Back to Home
        </button>
      </div>
    );
  }

  // Extract languages as an array
  const languages = country.languages ? Object.values(country.languages) : [];

  // Extract currencies as an array
  const currencies = country.currencies 
    ? Object.values(country.currencies).map(currency => `${currency.name} (${currency.symbol || 'N/A'})`) 
    : [];

  // Extract border countries
  const borders = country.borders || [];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navigation */}
      <nav className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-gray-800">Countries Explorer</h1>
            </div>
            <div className="flex items-center">
              {user ? (
                <div className="flex items-center">
                  <span className="mr-4">Welcome, {user.name}</span>
                  <button
                    onClick={logout}
                    className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700 transition duration-300"
                  >
                    Logout
                  </button>
                </div>
              ) : (
                <div className="flex items-center space-x-4">
                  <Link
                    to="/login"
                    className="px-4 py-2 rounded border border-blue-600 text-blue-600 hover:bg-blue-50 transition duration-300"
                  >
                    Sign In
                  </Link>
                  <Link
                    to="/register"
                    className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700 transition duration-300"
                  >
                    Sign Up
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Back Button */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <button 
          onClick={() => navigate('/')}
          className="px-6 py-2 bg-white shadow-md rounded-md flex items-center hover:bg-gray-50 transition duration-300"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
          </svg>
          Back
        </button>
      </div>

      {/* Country Details */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <div className="md:flex">
            <div className="md:w-1/2">
              <img 
                src={country.flags.svg} 
                alt={`Flag of ${country.name.common}`}
                className="w-full h-auto object-cover"
              />
            </div>
            <div className="p-6 md:w-1/2">
              <h2 className="text-3xl font-bold mb-6">{country.name.common}</h2>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                <div>
                  <p className="mb-2"><span className="font-semibold">Official Name:</span> {country.name.official}</p>
                  <p className="mb-2"><span className="font-semibold">Population:</span> {country.population.toLocaleString()}</p>
                  <p className="mb-2"><span className="font-semibold">Region:</span> {country.region}</p>
                  <p className="mb-2"><span className="font-semibold">Sub Region:</span> {country.subregion || 'N/A'}</p>
                  <p className="mb-2"><span className="font-semibold">Capital:</span> {country.capital?.[0] || 'N/A'}</p>
                </div>

                <div>
                  <p className="mb-2"><span className="font-semibold">Top Level Domain:</span> {country.tld?.[0] || 'N/A'}</p>
                  <p className="mb-2">
                    <span className="font-semibold">Currencies:</span>{' '}
                    {currencies.length > 0 ? currencies.join(', ') : 'N/A'}
                  </p>
                  <p className="mb-2">
                    <span className="font-semibold">Languages:</span>{' '}
                    {languages.length > 0 ? languages.join(', ') : 'N/A'}
                  </p>
                  <p className="mb-2">
                    <span className="font-semibold">Area:</span>{' '}
                    {country.area ? `${country.area.toLocaleString()} km²` : 'N/A'}
                  </p>
                </div>
              </div>

              {/* Border Countries */}
              <div className="mt-8">
                <h3 className="text-xl font-semibold mb-3">Border Countries:</h3>
                <div className="flex flex-wrap gap-2">
                  {borders.length > 0 ? (
                    borders.map(border => (
                      <Link
                        key={border}
                        to={`/country/${border}`}
                        className="px-4 py-1 bg-white shadow-sm border border-gray-200 rounded-md hover:bg-gray-50 transition duration-300"
                      >
                        {border}
                      </Link>
                    ))
                  ) : (
                    <p>No bordering countries</p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Map Section */}
          <div className="p-6 border-t border-gray-200">
            <h3 className="text-xl font-semibold mb-3">Location:</h3>
            {country.maps?.googleMaps && (
              <div className="mt-4">
                <a 
                  href={country.maps.googleMaps} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 inline-block"
                >
                  View on Google Maps
                </a>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-white shadow-inner py-6 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <p className="text-center text-gray-500">
            &copy; {new Date().getFullYear()} Countries Explorer. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default CountryDetailPage;
