import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../App';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/users/login', {
        email,
        password,
      });

      const { token, user } = response.data;

      // Save to localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      // Update auth context
      login(user, token);

      // Redirect to home page
      navigate('/home');
    } catch (err) {
      setError(
        err.response?.data?.message || 'An error occurred during login'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center relative overflow-hidden">
      {/* Orb Effects */}
      <div className="orb bg-blue-500 w-96 h-96 top-1/4 -left-48"></div>
      <div className="orb bg-purple-500 w-80 h-80 bottom-1/4 -right-40"></div>

      <div className="w-full max-w-md p-8 relative z-10">
        <div className="text-center mb-8">
          <Link to="/" className="text-3xl font-bold tracking-tighter inline-block">
            <span className="text-white">Countries</span>
            <span className="text-blue-500">Explorer</span>
          </Link>
          <h2 className="text-2xl font-bold mt-6 font-mono">Sign In</h2>
          <p className="text-gray-400 mt-2">Welcome back! Please sign in to your account.</p>
        </div>

        <div className="bg-transparent border border-gray-800 rounded-lg p-8 shadow-lg backdrop-blur-sm">
          {error && (
            <div className="bg-red-900 bg-opacity-20 border border-red-800 text-red-300 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="mb-6">
              <label htmlFor="email" className="block text-gray-300 mb-2 font-mono">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
              />
            </div>

            <div className="mb-6">
              <div className="flex justify-between mb-2">
                <label htmlFor="password" className="text-gray-300 font-mono">
                  Password
                </label>
                <Link to="/" className="text-gray-400 text-sm hover:text-white">
                  Forgot password?
                </Link>
              </div>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
              />
            </div>

            <button
              type="submit"
              className="w-full py-3 px-4 bg-transparent border border-gray-700 rounded-full text-white font-medium hover:bg-gray-900 transition duration-300 disabled:opacity-50"
              disabled={loading}
            >
              {loading ? 'Signing in...' : 'Sign In'}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-400">
              Don't have an account?{' '}
              <Link to="/register" className="text-white hover:underline">
                Sign Up
              </Link>
            </p>
          </div>
        </div>

        <div className="mt-8 text-center">
          <Link to="/" className="text-gray-400 hover:text-white flex items-center justify-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Back to Landing Page
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
