import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../App';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { register } = useContext(AuthContext);
  const navigate = useNavigate();

  const { name, email, password, confirmPassword } = formData;

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    setError('');

    // Validate passwords match
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    setLoading(true);

    try {
      // Use the register function from AuthContext
      const result = await register(name, email, password);

      if (!result.success) {
        setError(result.error);
      } else {
        // Redirect to home page on success
        navigate('/home');
      }
    } catch (err) {
      setError('An error occurred during registration');
      console.error('Registration error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center relative overflow-hidden">
      {/* Orb Effects */}
      <div className="orb bg-purple-500 w-96 h-96 top-1/4 -left-48 opacity-30"></div>
      <div className="orb bg-pink-500 w-80 h-80 bottom-1/4 -right-40 opacity-30"></div>
      <div className="orb bg-purple-400 w-64 h-64 top-3/4 left-1/4 opacity-20"></div>
      <div className="orb bg-pink-400 w-72 h-72 top-1/3 right-1/4 opacity-20"></div>
      <div className="orb bg-purple-300 w-48 h-48 bottom-1/3 left-1/3 opacity-20"></div>

      <div className="w-full max-w-md p-8 relative z-10">
        <div className="text-center mb-8">
          <Link to="/" className="text-xl font-bold tracking-tighter inline-block">
            <span className="text-white">Countries</span>
            <span className="text-purple-500">Explorer</span>
          </Link>
          <h2 className="text-lg font-bold mt-6 font-mono">Create Account</h2>
          <p className="text-gray-400 text-sm mt-2">Join us to explore countries around the world.</p>
        </div>

        <div className="bg-transparent border border-gray-800 rounded-lg p-8 shadow-lg backdrop-blur-sm">
          {error && (
            <div className="bg-red-900 bg-opacity-20 border border-red-800 text-red-300 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <form onSubmit={onSubmit}>
            <div className="mb-6">
              <label htmlFor="name" className="block text-gray-300 text-xs mb-2 font-mono">
                Full Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={onChange}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="email" className="block text-gray-300 text-xs mb-2 font-mono">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={onChange}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
              />
            </div>

            <div className="mb-6">
              <label htmlFor="password" className="block text-gray-300 text-xs mb-2 font-mono">
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={onChange}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
                minLength="6"
              />
            </div>

            <div className="mb-6">
              <label htmlFor="confirmPassword" className="block text-gray-300 text-xs mb-2 font-mono">
                Confirm Password
              </label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={confirmPassword}
                onChange={onChange}
                className="w-full px-4 py-2 bg-transparent border border-gray-700 rounded-md focus:outline-none focus:ring-1 focus:ring-white focus:border-gray-600"
                required
                minLength="6"
              />
            </div>

            <button 
              type="submit" 
              className="w-full bg-gradient-to-r from-purple-600 to-purple-400 text-white py-2 rounded-md hover:from-purple-700 hover:to-purple-500 transition-all"
              disabled={loading}
            >
              {loading ? 'Creating Account...' : 'Sign Up'}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-400 text-sm">
              Already have an account?{' '}
              <Link to="/login" className="text-white hover:underline">
                Sign In
              </Link>
            </p>
          </div>
        </div>

        <div className="mt-8 text-center">
          <Link to="/" className="text-gray-400 text-xs hover:text-white flex items-center justify-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Back to Landing Page
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
